package SizeofDataTypes;

public class size {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Size of int in Java: "+ (Integer.SIZE / 8) + " Byte");
		System.out.println("Size of long in Java: "+ (Long.SIZE / 8) + " Byte");
		System.out.println("Size of char in Java: "+ (Character.SIZE / 8) + " Byte");
		System.out.println("Size of float in Java: "+ (Float.SIZE / 8) + " Byte");
		System.out.println("Size of double in Java: "+ (Double.SIZE / 8) + " Byte");

	}

}
