// 9.Program to check if a given number is positive or negative

package HW_27_Jan;

public class Positive_negative_09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=-14;
		if(num>0) {
			System.out.println("Positive "+num);
		}else {
			System.out.println("Negative "+num);
		}

	}

}
