//write a program to declare all data type variables

package myprogram;

public class eight {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Boolean data type
		boolean first = true;
		System.out.println("Boolean data type: "+first);
		
		//byte data type
		byte second = 124;
		System.out.println("Byte data type: "+second);
		
		//short data type
		short third = -200;
		System.out.println("short data type: "+third);
		
		//int data type
		int four = -4250000;
		System.out.println("int data type: "+four);
		
		//long data type
		long five = -423322000000l;
		System.out.println("long data type: "+five);
		
		//double data type
		double six = -42.3;
		System.out.println("double data type: "+six);
		
		//float data type
		float seven = -45.5f;
		System.out.println("float data type: "+seven);
		
		//char data type
		char eight = '\u0051';
		System.out.println("char data type: "+eight);

	}

}
